$(document).ready(function(){var o=$(".scroll");o.click(function(o){o.preventDefault(),$("body,html").animate({scrollTop:$(this.hash).offset().top},1e3)}),$(window).scroll(function(){var t=$(this).scrollTop();o.each(function(){var o=$(this.hash).offset().top;t>=o&&($(this).parent().addClass("active"),$(this).parent().siblings().removeClass("active"))})}),$("body").prepend('<a href="#" class="back-to-top">Back to Top</a>');var t=300;$(window).scroll(function(){$(window).scrollTop()>t?$("a.back-to-top").fadeIn("slow"):$("a.back-to-top").fadeOut("slow")}),$("a.back-to-top, a.simple-back-to-top").click(function(){return $("html, body").animate({scrollTop:0},700),!1})});